function drawBoard() {
    ctx.lineWidth = "2";
    ctx.strokeStyle = "black";
    for (let i = 0; i < 5; i++) {
        for (let j = 0; j < 5; j++) {
            let startboxx = startx + j * boxwidth,
                startboxy = starty + i * boxheight;
            ctx.strokeRect(startboxx, startboxy, boxwidth, boxheight);
            if (((i == 0 || i == 4) && j == 2) || ((j == 0 || j == 4) && i == 2) || (i == 2 && j == 2)) {
                ctx.beginPath();
                ctx.moveTo(startboxx, startboxy);
                if (j == 0 && i + j != 4) ctx.strokeStyle = playercolor[4];
                if (i == 0 && i + j != 4) ctx.strokeStyle = playercolor[3];
                if (j == 4 && i + j != 4) ctx.strokeStyle = playercolor[2];
                if (i == 4 && i + j != 4) ctx.strokeStyle = playercolor[1];
                ctx.lineTo(startboxx + boxwidth, startboxy + boxheight);
                ctx.moveTo(startboxx, startboxy + boxheight);
                ctx.lineTo(startboxx + boxwidth, startboxy);
                ctx.closePath();
                ctx.stroke();
                ctx.strokeStyle = "black";
            }
        }
    }
}